import React, { useEffect, useState } from 'react'

export default function EachMerchant(props) {

    // console.log(props.duration);
    // console.log(props.time);
    // console.log(+props.duration - Math.floor((new Date(props.time) / 1000 / 60) % 60))
    var difference;
    const calculateTimeLeft = () => {

        const uploadTime = new Date(props.time)
        uploadTime.setMinutes(uploadTime.getMinutes() + props.duration)
        difference = +uploadTime - +new Date();

        let timeLeft = {};
        if (difference > 0) {
            timeLeft = {
                hours: Math.floor((difference / (1000 * 60 * 60)) % 24),
                minutes: Math.floor((difference / 1000 / 60) % 60),
                seconds: Math.floor((difference / 1000) % 60)
            };
            return timeLeft
        } else if (difference < 0) {
            timeLeft = 0

            fetch('/togglelisting', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ listing_id: props.listing_id, boolean: false })
            })
                .then((res) => res.text())
                .then((res) => {
                    console.log(res)
                    return timeLeft
                })
            // console.log(timeLeft)

        }

    };

    const [mount, setMount] = useState('True')

    // useEffect(() => {
    //     return () => {
    //         console.log('this is in mount useeffect')
    //         props.unmount()
    //     }
    // }, [mount])



    const [timeLeft, setTimeLeft] = useState(calculateTimeLeft());

    // if (timeLeft) {
    //     if (timeLeft.hours === 0 && timeLeft.minutes === 0 && timeLeft.seconds === 0) {
    //         console.log("help in mount")
    //         setMount(!mount)
    //     }
    // }

    useEffect(() => {
        const timer = setTimeout(() => {
            setTimeLeft(calculateTimeLeft());
        }, 1000);
        return () => {
            props.what(timeLeft, props.merchant_Id);

            clearTimeout(timer);
        }
    });

    const timerComponents = [];


    Object.keys(timeLeft).forEach((interval) => {
        if (!timeLeft[interval]) {
            return;
        }

        timerComponents.push(
            <span>
                {timeLeft[interval]} {interval}{" "}
            </span>
        );
    });

    // if (timeLeft.hours == 0 && timeLeft.minutes == 0 && timeLeft.seconds == 0) {

    //     return null
    // } else {

    return (
        <>
            {
                timerComponents.length ? <div key={props.index}>
                    <h1>{props.children}</h1> {timerComponents}</div> : null
            }
        </>
    )
}
