import React, { Component } from 'react'
import EachMerchant from './EachMerchant/EachMerchant'
import OrderHistory from '../OrderHistory/OrderHistory'
import { Route, Link, Redirect, Switch } from 'react-router-dom'
import IndivListing from '../IndivStore/IndivListing'
import ListingContainer from '../IndivStore/ListingContainer'
export default class TimeLine extends Component {
    constructor(props) {
        super()
        this.state = {
            timeLine: [],
            cardUnmount: false,
            length: 20
        }
    }
    componentDidMount() {
        fetch('/timeline')
            .then(res => res.json())
            .then(res => {
                console.log(res)
                this.setState({
                    timeLine: res
                })
            })
    }
    testing = (timerem) => {

    }
    componentDidUpdate(prevState) {
        console.log('hi 1')
        if (this.state.length !== prevState.length) {
            console.log('hi')
            fetch('/timeline')
                .then(res => res.json())
                .then(res => {
                    console.log(res)
                    this.setState({
                        timeLine: res
                    })
                })
        }
    }
    // merchantCardUnmountHandler = () => {
    //     console.log('hi callback function')
    //     fetch('/timeline')
    //         .then(res => res.json())
    //         .then(res => {
    //             console.log(res, `response fired`)
    //             this.setState({
    //                 timeLine: res
    //             })
    //         })
    // }

    render() {
        if (this.state.timeLine.length > 20) {
            this.state.timeLine.splice(19, this.state.timeLine.length - 20)
        }

        this.state.timeLine.sort(function (a, b) {
            const uploadTimeA = new Date(a.time)
            uploadTimeA.setMinutes(uploadTimeA.getMinutes() + a.time)
            const uploadTimeB = new Date(b.time)
            uploadTimeB.setMinutes(uploadTimeB.getMinutes() + b.time)
            return uploadTimeA - uploadTimeB
        })

        const merchantCard = this.state.timeLine.map((eachCard, index) => {
            const discount = (eachCard.unit_price - eachCard.price_floor) / eachCard.unit_price * 100
            let path = "/" + eachCard.merchant_id
            return <Link to={path}>

                <EachMerchant key={index} duration={eachCard.time_limit_min} time={eachCard.time} merchant_Id={eachCard.merchant_id} unmount={this.merchantCardUnmountHandler}>
                    <div>{eachCard.name}</div>
                    <div>up to{discount}%</div>
                </EachMerchant>

            </Link>
        })
        this.setState({ length: merchantCard.length })
        const routeArray = this.state.timeLine.map((eachCard, index) => {
            let path = "/" + eachCard.merchant_id
            return <Route path={path} render={
                () => <ListingContainer merchant_id={eachCard.merchant_id} stripper={this.props.stripper} />
            } />
        })
        return (
            <div>
                <div className="navbar">
                    <div>Image here</div>
                    <Link className="login break" to="/Timeline">Timeline</Link>
                    <Link className="login break" to="/Orderhistory">Order history</Link>
                </div>
                <main>
                    <Switch>
                        {routeArray}
                        <Route path="/Orderhistory" render={
                            () => <OrderHistory />
                        } />
                        <Route path="/" render={
                            () => <div>{merchantCard}</div>
                        } />
                    </Switch>
                </main>
            </div >
        )
    }
}
