import React, { Component } from 'react'
import EachMerchant from './EachMerchant/EachMerchant'
import OrderHistory from '../OrderHistory/OrderHistory'
import { Route, Link, Redirect, Switch } from 'react-router-dom'
import IndivListing from '../IndivStore/IndivListing'
import ListingContainer from '../IndivStore/ListingContainer'

export default class TimeLine extends Component {
    constructor(props) {
        super()
        this.state = {
            timeLine: [],
            refresh: false,
            fetch: false
        }
    }
    componentDidMount() {
        fetch('/timeline')
            .then(res => res.json())
            .then(res => {
                console.log(res);
                this.setState({
                    timeLine: res
                })
            })
    }
    testing = (prop) => {
        console.log(prop)
        if (prop.hours === 0 && prop.minutes === 0 && prop.seconds === 0 && !this.state.fetch) {
            console.log(`in if`)
            fetch('/timeline')
                .then(res => res.json())
                .then(res => {
                    console.log(res)
                    this.setState({
                        timeLine: res,
                        fetch: !this.state.fetch

                    })
                })
        }
    }
    // testing2 = () => {
    //     if (this.state.refresh === true) {
    //         console.log('hi')
    //         fetch('/timeline')
    //             .then(res => res.json())
    //             .then(res => {
    //                 console.log(res)
    //                 this.setState({
    //                     timeLine: res,
    //                     refresh: !this.state.refresh

    //                 })
    //             })
    //     }
    // }
    // componentDidUpdate(prevState) {
    //     console.log('hi 1')
    //     if (this.state.refresh === true) {
    //         console.log('hi')
    //         fetch('/timeline')
    //             .then(res => res.json())
    //             .then(res => {
    //                 console.log(res)
    //                 this.setState({
    //                     timeLine: res,
    //                     refresh: !this.state.refresh

    //                 })
    //             })
    //     }
    // }
    // merchantCardUnmountHandler = () => {
    //     console.log('hi callback function')
    //     fetch('/timeline')
    //         .then(res => res.json())
    //         .then(res => {
    //             console.log(res, `response fired`)
    //             this.setState({
    //                 timeLine: res
    //             })
    //         })
    // }

    render() {
        let newArray = this.state.timeLine.map((item, index) => {
            const uploadTime = new Date(item.time)
            uploadTime.setMinutes(uploadTime.getMinutes() + item.time_limit_min)
            let difference = +uploadTime - +new Date();
            if (difference > 0) {
                return item
            }
        })
        console.log(newArray)
        if (newArray.length > 20) {
            newArray.splice(19, newArray.length - 20)
        }

        newArray.sort(function (a, b) {
            const uploadTimeA = new Date(a.time)
            uploadTimeA.setMinutes(uploadTimeA.getMinutes() + a.time_limit_min)
            const uploadTimeB = new Date(b.time)
            uploadTimeB.setMinutes(uploadTimeB.getMinutes() + b.time_limit_min)
            return uploadTimeA - uploadTimeB
        })

        let merchantCard = newArray.map((eachCard, index) => {
            const discount = (eachCard.unit_price - eachCard.price_floor) / eachCard.unit_price * 100
            let path = "/" + eachCard.merchant_id
            return <Link to={path}>

                <EachMerchant key={index} duration={eachCard.time_limit_min} time={eachCard.time} merchant_Id={eachCard.merchant_id} unmount={this.merchantCardUnmountHandler} what={this.testing}>
                    <div>{eachCard.name}</div>
                    <div>up to{discount}%</div>
                </EachMerchant>

            </Link>
        })

        const routeArray = newArray.map((eachCard, index) => {
            let path = "/" + eachCard.merchant_id
            return <Route path={path} render={
                () => <ListingContainer merchant_id={eachCard.merchant_id} stripper={this.props.stripper} />
            } />
        })
        return (
            <div>
                <div className="navbar">
                    <div>Image here</div>
                    <Link className="login break" to="/Timeline">Timeline</Link>
                    <Link className="login break" to="/Orderhistory">Order history</Link>
                </div>
                <main>
                    <Switch>
                        {routeArray}
                        <Route path="/Orderhistory" render={
                            () => <OrderHistory />
                        } />
                        <Route path="/" render={
                            () => <div>{merchantCard}</div>
                        } />
                    </Switch>
                </main>
            </div >
        )
    }
}
