import React, { Component } from "react";
import { withRouter } from 'react-router'
import { Form, Col, Button } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

class CreateItem extends Component {
    constructor() {
        super();
        this.state = { fields: {}, errors: {} }
    }


    onSubmitHandler(e) {
        e.preventDefault()
        let data = {
            item_name: e.target.item_name.value,
            unit_price: e.target.unit_price.value,
            quantity: e.target.quantity.value,
            price_ceiling: e.target.price_ceiling.value,
            price_floor: e.target.price_floor.value,
            description: e.target.description.value,
            category_id: e.target.category_id.value,
            time_limit_min: e.target.time_limit_min.value,
            merchant_id: e.target.merchant_id.value
        }
        fetch('/newListing', {
            method: "POST",
            headers: {
                "Content-type": "application/json"
            },
            body: JSON.stringify(data),
        }).then(res =>
            res.json()
        ).then(res => {
            console.log(res)
            this.props.onClick();
        })
    }

    fieldValidation() {
        let fields = this.state.fields;
        let errors = {};
        let dataValidity = true;

        if (!fields["item_name"]) {
            dataValidity = false;
            errors["item_name"] = "Cannot be Empty"
        }

        if (!fields["unit_price"]) {
            dataValidity = false;
            errors["unit_price"] = "Cannot be Empty"
        }
        if (!fields["quantity"]) {
            dataValidity = false;
            errors["quantity"] = "Cannot be Empty"
        }
        if (!fields["price_ceiling"]) {
            dataValidity = false;
            errors["price_ceiling"] = "Cannot be Empty"
        }
        if (!fields["price_floor"]) {
            dataValidity = false;
            errors["price_floor"] = "Cannot be Empty"
        }
        if (!fields["item_name"]) {
            dataValidity = false;
            errors["item_name"] = "Cannot be Empty"
        }
        if (!fields["item_name"]) {
            dataValidity = false;
            errors["item_name"] = "Cannot be Empty"
        }
        this.setState({ errors: errors })
        return dataValidity
    }
    onChangeHandler(e, field) {
        let fields = this.state.fields;
        fields[field] = e.target.value;
        this.setState({ fields });
    }


    render() {
        return (
            <Form action="/newListing" onSubmit={(e) => this.onSubmitHandler(e)} method="post">
                <Form.Row>
                    <Form.Label column lg={2}>Item Name</Form.Label>
                    <Col>
                        <Form.Control onChange={(e) => { this.onChangeHandler(e, "item_name") }} type="text" placeholder="Item Name" name="item_name" />
                    </Col>
                </Form.Row>
                <br />
                <Form.Row>
                    <Form.Label column lg={2}>Unit Price</Form.Label>
                    <Col>
                        <Form.Control onChange={(e) => { this.onChangeHandler(e, "unit_price") }} type="text" placeholder="Unit Price" name="unit_price" />
                    </Col>
                </Form.Row>
                <br />
                <Form.Row>
                    <Form.Label column lg={2}>Quantity</Form.Label>
                    <Col>
                        <Form.Control onChange={(e) => { this.onChangeHandler(e, "quantity") }} type="text" placeholder="Quantity" name="quantity" />
                    </Col>
                </Form.Row>
                <br />
                <Form.Row>
                    <Form.Label column lg={2}>Maximum Price</Form.Label>
                    <Col>
                        <Form.Control onChange={(e) => { this.onChangeHandler(e, "price_ceiling") }} type="text" placeholder="maximum price" name="price_ceiling" />
                    </Col>
                </Form.Row>
                <br />
                <Form.Row>
                    <Form.Label column lg={2}>Minimum Price</Form.Label>
                    <Col>
                        <Form.Control onChange={(e) => { this.onChangeHandler(e, "price_floor") }} type="text" placeholder="minimum price" name="price_floor" />
                    </Col>
                </Form.Row>
                <br />
                <Form.Row>
                    <Form.Label column lg={2}>Category</Form.Label>
                    <Col>
                        <Form.Control name="category_id" as="select" custom>
                            <option value="1">Snacks</option>
                            <option value="2">Light Bites</option>
                            <option value="3">Dim Sum</option>
                            <option value="4">Noodles</option>
                            <option value="5">Junk Food</option>
                            <option value="6">Hearty Meals</option>
                            <option value="7">Snacks</option>
                            <option value="8">Light Bites</option>
                            <option value="9">Dim Sum</option>
                            <option value="10">Noodles</option>
                            <option value="11">Junk Food</option>
                            <option value="12">Hearty Meals</option>
                        </Form.Control>
                    </Col>
                </Form.Row>
                <br />
                <Form.Row>
                    <Form.Label column lg={2}>Description</Form.Label>
                    <Col>
                        <Form.Control onChange={(e) => { this.onChangeHandler(e, "description") }} type="text" placeholder="Description" name="description" />
                    </Col>
                </Form.Row>
                <br />
                <Form.Row>
                    <Form.Label column lg={2}>Duration</Form.Label>
                    <Col>
                        <Form.Control onChange={(e) => { this.onChangeHandler(e, "time_limit_min") }} type="text" placeholder="Time in Minutes" name="time_limit_min" />
                    </Col>
                </Form.Row>
                <Button type="submit" onClick={this.props.onClick} className="mb-2">
                    Submit
                </Button>
                <Col>
                    <Form.Control type="hidden" placeholder="Normal text" name="merchant_id" value={this.props.merchant_id} />
                </Col>
            </Form>

        )
    }

}

export default withRouter(CreateItem)
