import React from "react";
import ReactDOM from "react-dom"

export default class Modal extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            username: "",
            email: "",
            password: ""
        }
    }
    componentDidMount() {
        document.addEventListener('mousedown', this.handleClick);
    }

    componentWillUnmount() {
        document.removeEventListener('mousedown', this.handleClick);
    }
    handleClick=(e)=> {
        if(this.node.contains(e.target)) {
            return
        }

        this.handleClickOutside(this.props.onClose);
    }
    onChange=(e)=> {
         this.setState({
            [e.target.name]: e.target.value
        });
    }
    onSubmit=(e)=> {
         e.preventDefault();
         const { username, password, email } = this.state;
             const data = {
                  username,
                  password,
                  email,
                };
         fetch('/home/register/user',{
            method: "POST",
            headers: {
                "Content-type": "application/json"
            },
            body: JSON.stringify(data),
        }).then(res=>
            res.json()
        ).then(res=> {
            console.log(res)
            // if the user acctually successfully registered
            if(res.userId && res.userName) {
                let registered = this.props.onRegistered;
                registered(res.userId, res.userName);
            }
        }).catch(err=> {
            console.log(err)
          })
    }
    render() {
    if(!this.props.open) return null
    return (
        <>
            <div className="overlayBackground" >
                <div className="overlay" onClick={this.props.onClose}>
                    <div className="formsParent" >
                        <div>Sign Up as a New User</div>
                            <form onSubmit={this.onSubmit} className="forms" >
                                <input type="text" name="username" placeholder="Username" onChange={this.onChange}/>
                                <br/>
                                <input type="text" name="email" placeholder="Email" onChange={this.onChange}/>
                                <br/>
                                <input type="password" name="password" placeholder="password" onChange={this.onChange}/>
                                <br/>
                                <input type="submit" value ="Submit"/>
                            </form>
                    </div>
                </div>
            </div>
        </>
        )
    }
}
