import React, { Component } from "react";
import { Bar, Line, Pie, HorizontalBar } from "react-chartjs-2";

class Popularity extends Component {
  constructor(props) {
    super(props);
  }

  render() {
    return (
      <div>
        <div className="chart">
          <HorizontalBar
            data={{
              labels: this.props.ratedListingsName,
              datasets: [
                {
                  label: "Best Rated",
                  backgroundColor: "rgba(255,99,132,0.2)",
                  borderColor: "rgba(255,99,132,1)",
                  borderWidth: 1,
                  hoverBackgroundColor: "rgba(255,99,132,0.4)",
                  hoverBorderColor: "rgba(255,99,132,1)",
                  data: this.props.averageRatePerListing,
                },
              ],
            }}
          />
        </div>

        <h1>Your average rating: {this.props.merchantRating}</h1>
      </div>
    );
  }
}

export default Popularity;
