import React, { Component } from "react";
import RatingStars from "./RatingStars";

class RatingStarsContainer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      listing_id: 1,
      merchant_id: 1,
      user_id: 1,
      clicked: false,
      disappear: false,
      rating: 0,
    };
    this.handleClick = this.handleClick.bind(this);
  }

  //handle click function: set state clicked = true, disappear = true, inner TEXT to be ' thanks for rating'!
  handleClick(e) {
    this.setState({ clicked: true, rating: e.target.value });
  }

  componentDidMount() {
    if ((this.state.clicked = true)) {
      fetch(`/ratelisting`, {
        method: "POST",
        header: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          user_id: this.state.user_id,
          listing_id: this.state.listing_id,
          merchant_id: this.state.merchant_id,
          rating: this.state.rating,
        }),
      })
        .then((res) => res.json())
        .then((res) =>
          this.setState({
            rated: true,
            disappear: true,
          })
        );
    }
  }
  //Unmount when disappear = true

  render() {
    if (!this.state.disappear) {
      return (
        <div>
          <RatingStars onClick={this.handleClick} />
        </div>
      );
    } else {
      return;
      <div>Thanks for rating!</div>;
    }
  }
}

export default RatingStarsContainer;
