import React, { Component } from "react";

class RatingStars extends Component {
  constructor(props) {
    super(props);
    this.state = {
      rated: false,
      displayStar: props.displayStar,
      receipt_id: props.receipt_id,
      user_id: props.user_id,
      merchant_id: props.merchant_id,
    };
  }

  render() {
    if (this.state.displayStar) {
      if (!this.state.rated) {
        return (
          <div>
            <button onClick={(e) => this.props.onClick(e)} value="5" id="5">
              ☆
            </button>
            <button onClick={(e) => this.props.onClick(e)} value="4" id="4">
              ☆
            </button>
            <button onClick={(e) => this.props.onClick(e)} value="3" id="3">
              ☆
            </button>
            <button onClick={(e) => this.props.onClick(e)} value="2" id="2">
              ☆
            </button>
            <button onClick={(e) => this.props.onClick(e)} value="1" id="1">
              ☆
            </button>
          </div>
        );
      }
    } else if (!this.props.display && !this.state.rated) {
      return <div>You have already rated this!</div>;
    } else if (this.props.display && this.state.rated) {
      return <div>Thanks for your feedback</div>;
    } else {
      return <div>You have already rated this!</div>;
    }
  }
}

export default RatingStars;
