import React, { Component } from "react";
import Bestselling from "./Bestselling";
import Revenue from "./Revenue";

class DashboardContainer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      merchant_id: this.props.merchant_id,
      display: true,
      res: "",
      totalRev: 0,
      revMax: 0,
    };
  }

  //fetch from backend data to pass down as props to children
  componentDidMount() {
    //mounting
    if (this.state.display) {
      fetch("/merchantorders/1")
        .then((res) => res.json())
        .then((res) => {
          if (res.length < 1) {
            console.log("no orders yet");
          } else {
            console.log(res);
            let revTotal = res.reduce(function (prev, cur) {
              return prev + cur.revenue;
            }, 0);
            let revMax = res.reduce(function (prev, current) {
              return prev.revenue > current.revenue ? prev : current;
            });

            let revItem = res.reduce(function (prev, current) {
              if (prev.item_name === current.item_name) {
                return prev.revenue > current.revenue ? prev : current;
              }
            });

            console.log(arr);

            console.log(revMax, "---revMax");
            console.log(revTotal, "----total revenue arr");
            this.setState({ res: res, totalRev: revTotal, revMax: revMax });
          }
        })
        .catch((error) => {
          console.log(
            "error happened--- at GET MERCHANT ORDER MOUNT",
            error.message
          );
        });
    }
  }

  render() {
    return (
      <div>
        Hello from Dashboard DashboardContainer
        <Bestselling
          res={this.state.res}
          totalRev={this.state.totalRev}
          revMax={this.state.revMax}
        />
        <Revenue res={this.state.res} />
      </div>
    );
  }
}

export default DashboardContainer;
