import React, { Component } from "react";
import { Bar, Line, Pie, HorizontalBar } from "react-chartjs-2";

class Revenue extends Component {
  constructor(props) {
    super(props);
    this.state = {};
    this.chartRef = React.createRef();
  }
  componentDidMount() {
    this.myChart = new Bar(this.canvasRef.current, {
      type: "bar",
      data: {
        labels: this.props.names,
        datasets: [
          {
            label: "Your Perfornmance",
            data: this.props.revenues,
            backgroundColor: "#70CAD1",
          },
        ],
      },
    });
  }
  render() {
    return (
      <div className="chart">
        <canvas ref={this.chartRef} />
      </div>
    );
  }
}

export default Revenue;
