import React, { Component } from "react";

class Bestselling extends Component {
  constructor(props) {
    super(props);
    this.state = {
      listings: [],
      display: true,
    };
  }

  //helper function to sort thru name array and get rid of the duplicate names

  componentDidMount() {
    //mounting
    if (this.state.display) {
      fetch("/all/listing/1")
        .then((response) => response.json())
        .then((response) => {
          if (response.length < 1) {
            console.log("no listings yet");
          } else {
            console.log(response);
            let res = response.array.forEach((element) => {
              let array = [];
              array.push(element.item_name);
              return array;
            });
            this.setState({ listings: res });
          }
        })
        .catch((error) => {
          console.log(
            "error happened--- at GET MERCHANT LISTINGS ORDER MOUNT",
            error.message
          );
        });
    }
  }

  render() {
    return (
      <div>
        <p>You earned ${this.props.totalRev} so far</p>
        <p>Your biggest order was {this.props.revMax.item_name}</p>
      </div>
    );
  }
}

export default Bestselling;
