import React, { Component } from "react";

class RatingStars extends Component {
  constructor(props) {
    super(props);
    this.state = {
      rated: false,
      displayStar: props.displayStar,
      submitted: false,
      receipt_id: props.receipt_id,
      user_id: props.user_id,
      merchant_id: props.merchant_id,
      rating: 0,
    };
    this.onSubmitHandler = this.onSubmitHandler.bind(this);
  }

  onSubmitHandler(e) {
    e.preventDefault();
    this.setState({ rated: true, submitted: true, rating: e.target.value });
  }
  componentDidUpdate(prevProps, prevState) {
    if (prevState.submitted !== this.state.submitted) {
      fetch("/ratelisting", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          rating_receipt_id: this.state.receipt_id,
          listing_id: this.props.listing_id,
          merchant_id: this.state.merchant_id,
          rating_stars: this.state.rating,
        }),
      })
        .then((res) => res.json())
        .then((res) => {
          this.setState({
            displayStar: false,
          });
        });
    }
  }

  render() {
    if (this.state.displayStar) {
      if (!this.state.rated) {
        return (
          <div>
            <button onClick={(e) => this.onSubmitHandler(e)} value="5" id="5">
              ☆
            </button>
            <button onClick={(e) => this.onSubmitHandler(e)} value="4" id="4">
              ☆
            </button>
            <button onClick={(e) => this.onSubmitHandler(e)} value="3" id="3">
              ☆
            </button>
            <button onClick={(e) => this.onSubmitHandler(e)} value="2" id="2">
              ☆
            </button>
            <button onClick={(e) => this.onSubmitHandler(e)} value="1" id="1">
              ☆
            </button>
          </div>
        );
      }
    } else if (!this.props.display && !this.state.rated) {
      return <div>You have already rated this!</div>;
    } else if (this.props.display && this.state.rated) {
      return <div>Thanks for your feedback</div>;
    }
  }
}

export default RatingStars;
