import React from 'react';
import IndivListing from './IndivListing';



export default class ListingContainer extends React.Component {
    constructor(props){
        //mounting
        super(props)
        console.log("----inside Listing constructor")

        this.state = {
            merchant_id: props.merchant_id,
            merchant_name: "",
            merchant_img: "",
            html: [],
            result: [],
            cart: []
        }
    }

//set state with props from parent: SEARCH
    // static getDerivedStateFromProps(nextProps,prevState) {
    //     //mounting
    //     return { display : nextProps.display}
    // }


//when state is changed, FETCH results from aPI
//side effects ie: HTTP requests are allowed here
    componentDidMount() {
        //mounting
        if(this.props.display) {
            let url = `/indivshop/1`
        fetch(url)
        .then(res => res.json())
        .then(res => {
            if(res.length < 1){
                res.send("ERROR AT MOUNT")
            } else {
                this.setState({merchant_name: res[0].name})
                let formattedResult = this.format(res);
                this.setState({result: res, html:formattedResult})
            }
        })
        .catch(error => {
        console.log("error happened", error.message)
    });
    }
        console.log("----inside componentDidMount")
    }

//add to cart click handler
//  addToCart = (item) => {
//     this.setState((prevState)=>{
//         cart: [...prevState.cart,item]
//     })
// }



//helper functions

//take the res.json and convert into nice HTML
format(array) {
    return array.map((item,index)=>{
        let item_name = item.item_name;
        let quantity = item.quantity;
        let discPrice = item.price_ceiling;
        let originalPrice =item.unit_price;
        let discount = (originalPrice-discPrice)/originalPrice
        let merchant_name = item.name;
        let cuisine = item.cuisine

        return <div>
        <IndivListing item_name={item_name} quantity={quantity} discPrice ={discPrice} originalPrice={originalPrice} discount={discount} merchant_name={merchant_name} cuisine={cuisine} key={index} onClick ={this.addToCart}/>

        </div>
    })
}




    render(){


        return (
            <>
            <button>View Cart {this.state.cart.length}</button>
                <h1>You are viewing deals from {this.state.merchant_name}</h1>
                <br/>

                <div className="ListItems" >
                {this.state.html}
                </div>


            </>
            )
    }
}
