import React, { Component } from "react";
import Bestselling from "./Bestselling";

class DashboardContainer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      merchant_id: this.props.merchant_id,
      display: true,
      res: "",
      totalRev: 0,
    };
  }

  //fetch from backend data to pass down as props to children
  componentDidMount() {
    //mounting
    if (this.state.display) {
      fetch("/merchantorders/1")
        .then((res) => res.json())
        .then((res) => {
          if (res.length < 1) {
            console.log("no orders yet");
          } else {
            console.log(res);
            let revTotal = res.reduce(function (prev, cur) {
              return prev + cur.revenue;
            }, 0);
            let revMax = Math.max.apply(
              Math,
              array.map(function (res) {
                return res.revenue;
              })
            );
            console.log(revTotal, "----total revenue arr");
            this.setState({ res: res, totalRev: revTotal });
          }
        })
        .catch((error) => {
          console.log(
            "error happened--- at GET MERCHANT ORDER MOUNT",
            error.message
          );
        });
    }
  }

  render() {
    return (
      <div>
        Hello from Dashboard DashboardContainer
        <Bestselling res={this.state.res} totalRev={this.state.totalRev} />
      </div>
    );
  }
}

export default DashboardContainer;
