import React, { Component } from "react";
import Bestselling from "./Bestselling";

class DashboardContainer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      merchant_id: this.props.merchant_id,
      display: true,
      res: "",
    };
  }

  //fetch from backend data to pass down as props to children
  componentDidMount() {
    //mounting
    if (this.state.display) {
      fetch("/merchantorders/1")
        .then((res) => res.json())
        .then((res) => {
          if (res.length < 1) {
            console.log("no orders yet");
          } else {
            console.log(res);
            let total = this.totalRev(res);
            console.log(total, "----total revenue arr");
            this.setState({ res: res });
          }
        })
        .catch((error) => {
          console.log(
            "error happened--- at GET MERCHANT ORDER MOUNT",
            error.message
          );
        });
    }
  }

  //get total revenue

  totalRev(array) {
    array.map((item) => {
      return item.quantity * item.price;
    });
  }

