import React, { Component } from "react";

class RatingStars extends Component {
  constructor(props) {
    super(props);
    this.state = {
      listing_id: 1,
      merchant_id: 1,
      user_id: 1,
    };
  }
  render() {
    return (
      <div>
        <form>
          <div className="rating">
            <p>Leave a review!</p>
            <input type="text" placeholder="Tell us what you think" />
            <input
              type="submit"
              name="rating"
              value="5"
              id="5"
              onClick={(e) => {
                e.persist();
                this.props.onClick(e);
              }}
            />
            <label for="5">☆</label>
            <input
              type="submit"
              name="rating"
              value="4"
              id="4"
              onClick={(e) => {
                e.persist();
                this.props.onClick(e);
              }}
            />
            <label for="4">☆</label>
            <input
              type="submit"
              name="rating"
              value="3"
              id="3"
              onClick={(e) => {
                e.persist();
                this.props.onClick(e);
              }}
            />
            <label for="3">☆</label>
            <input
              type="submit"
              name="rating"
              value="2"
              id="2"
              onClick={(e) => {
                e.persist();
                this.props.onClick(e);
              }}
            />
            <label for="2">☆</label>
            <input
              type="submit"
              name="rating"
              value="1"
              id="1"
              onClick={(e) => {
                e.persist();
                this.props.onClick(e);
              }}
            />
            <label for="1">☆</label>
          </div>
        </form>
      </div>
    );
  }
}

export default RatingStars;
