import React, { Component } from 'react'

let calculateTimeLeft = () => {
    let year = new Date().getFullYear();
    const difference = +new Date(`10/01/${year}`) - +new Date();
    let timeLeft = {};
    if (difference > 0) {
        timeLeft = {
            days: Math.floor(difference / (1000 * 60 * 60 * 24)),
            hours: Math.floor((difference / (1000 * 60 * 60)) % 24),
            minutes: Math.floor((difference / 1000 / 60) % 60),
            seconds: Math.floor((difference / 1000) % 60)
        };
    }
    return timeLeft
};

export default class TimeLine extends Component {

    constructor() {
        super()
        this.state = {
            timeLine: []
        }
    }



    componentDidMount() {
        fetch('/timeline')
            .then(res => res.json())
            .then(res => {
                console.log(res)
                this.setState({
                    timeLine: res
                })
            })
    }

    render() {
        calculateTimeLeft()
        const merchantCard = this.state.timeLine.map((eachCard, index) => {
            const discount = (eachCard.unit_price - eachCard.price_floor) / eachCard.unit_price * 100

            return <>
                <div>{eachCard.name}</div>
                <div>up to{discount}%</div>
            </>
        })
        return (
            <div>
                {timeleft}
                {merchantCard}
            </div>
        )
    }
}
